package com.effort.composestudy.basiclayout.image

import android.os.Bundle
import androidx.activity.ComponentActivity
import androidx.activity.compose.setContent
import androidx.compose.foundation.Image
import androidx.compose.foundation.layout.fillMaxSize
import androidx.compose.runtime.Composable
import androidx.compose.ui.Modifier
import androidx.compose.ui.res.painterResource
import androidx.compose.ui.tooling.preview.Preview
import coil3.compose.AsyncImage
import com.effort.composestudy.R
import com.effort.composestudy.ui.theme.ComposeOnlineStudyTheme

class ImageActivity : ComponentActivity() {

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContent {
            ComposeOnlineStudyTheme {
                MyImageTwo()
            }
        }
    }
}

@Composable
fun MyImageOne() {
    Image(
        painter = painterResource(id = R.drawable.mancity),
        contentDescription = "mancity"
    )
}

@Composable
fun MyImageTwo() {
    AsyncImage(
        model = "data:image/jpeg;base64,",
        contentDescription = "Champions league",
        modifier = Modifier.fillMaxSize()
    )
}

@Preview(showBackground = true)
@Composable
fun PreviewMyImage() {
    ComposeOnlineStudyTheme {
        MyImageTwo()
    }
}